import React, { useState, useEffect } from "react";
import "../css/Profile.css";

const Profile = ({ onLogout }) => {
  const [user, setUser] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({ name: "", phone: "", address: "" });

  // Load dummy user data
  useEffect(() => {
    const dummyUser = {
      name: "Dummy Name",
      email: "dummy@gmail.com",
      phone: "0123456789",
      address: "123 Demo Street, Demo",
    };
    setUser(dummyUser);
    setFormData({
      name: dummyUser.name,
      phone: dummyUser.phone,
      address: dummyUser.address,
    });
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setUser({ ...user, ...formData });
    setEditMode(false);
    alert("Profile updated successfully (dummy)!");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    onLogout && onLogout();
  };

  if (!user) return <div className="loading">Loading profile...</div>;

  return (
    <div className="page">
      <div className="container">
        <header className="header">
          <h1>User Profile</h1>
          <button onClick={handleLogout} className="logout">
            Log Out
          </button>
        </header>

        {editMode ? (
          <form onSubmit={handleSubmit} className="form">
            <label>
              Name
              <input type="text" name="name" value={formData.name} onChange={handleChange} />
            </label>
            <label>
              Email
              <input type="email" value={user.email} disabled />
            </label>
            <label>
              Phone
              <input type="tel" name="phone" value={formData.phone} onChange={handleChange} />
            </label>
            <label>
              Address
              <textarea name="address" rows="3" value={formData.address} onChange={handleChange}></textarea>
            </label>
            <div className="actions">
              <button type="button" className="cancel" onClick={() => setEditMode(false)}>Cancel</button>
              <button type="submit" className="save">Save Changes</button>
            </div>
          </form>
        ) : (
          <div className="info">
            <div><strong>Name:</strong> {user.name}</div>
            <div><strong>Email:</strong> {user.email}</div>
            <div><strong>Phone:</strong> {user.phone}</div>
            <div><strong>Address:</strong> {user.address}</div>
            <button onClick={() => setEditMode(true)} className="edit">Edit Profile</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
