import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/Login.css";

const API_URL = "http://localhost:5000/api/auth";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({});

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) navigate("/profile"); // Redirect to profile if logged in
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.email || !formData.password) {
      setErrors({ form: "Email and password are required" });
      return;
    }

    try {
      const res = await axios.post(`${API_URL}/login`, formData);
      // Store token in localStorage for persistent login
      localStorage.setItem("token", res.data.token);
      navigate("/profile"); // Redirect to profile after login
    } catch (err) {
      setErrors({ form: err.response?.data?.message || "Login failed" });
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {errors.form && <p className="error">{errors.form}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
        />
        <button type="submit">Log In</button>
      </form>
    </div>
  );
};

export default Login;
