import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "../css/Profile.module.css";

const API_URL = "http://localhost:5000/api/auth";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({ name: "", phone: "", address: "" });
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return navigate("/login");

    axios
      .get(`${API_URL}/profile`, { headers: { Authorization: `Bearer ${token}` } })
      .then((res) => {
        setUser(res.data.user);
        setFormData({
          name: res.data.user.name,
          phone: res.data.user.phone,
          address: res.data.user.address,
        });
      })
      .catch(() => navigate("/login"));
  }, [navigate]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleUpdate = () => {
    const token = localStorage.getItem("token");
    axios
      .put(`${API_URL}/profile`, formData, { headers: { Authorization: `Bearer ${token}` } })
      .then((res) => {
        setUser(res.data.user);
        setEditMode(false);
        alert("Profile updated successfully!");
      })
      .catch(() => alert("Update failed"));
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className={styles.profilePage}>
      <div className={styles.profileCard}>
        <h2 className={styles.title}>My Profile</h2>

        <p>
          <strong>Email:</strong> {user.email}
        </p>

        {editMode ? (
          <>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={styles.inputField}
            />
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className={styles.inputField}
            />
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              className={styles.inputField}
            />
            <div className={styles.buttonGroup}>
              <button onClick={handleUpdate} className={styles.saveBtn}>
                Save
              </button>
              <button onClick={() => setEditMode(false)} className={styles.cancelBtn}>
                Cancel
              </button>
            </div>
          </>
        ) : (
          <>
            <p>
              <strong>Name:</strong> {user.name}
            </p>
            <p>
              <strong>Phone:</strong> {user.phone}
            </p>
            <p>
              <strong>Address:</strong> {user.address}
            </p>
            <div className={styles.buttonGroup}>
              <button onClick={() => setEditMode(true)} className={styles.editBtn}>
                Edit Profile
              </button>
              <button onClick={handleLogout} className={styles.logoutBtn}>
                Logout
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
