import React, { useState } from 'react';
import styles from '../css/Registration.module.css';

const Registration = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', address: '', password: '', confirmPassword: ''
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
    if (!formData.phone.trim()) newErrors.phone = 'Phone number is required';
    else if (!/^\d{10,15}$/.test(formData.phone)) newErrors.phone = 'Phone number is invalid';
    if (!formData.address.trim()) newErrors.address = 'Address is required';
    if (!formData.password) newErrors.password = 'Password is required';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // TODO: replace alert with API registration call
    alert('Registration successful!');
    setFormData({ name:'', email:'', phone:'', address:'', password:'', confirmPassword:'' });
    setErrors({});
  };

  return (
    <div className={styles['registration-page']}>
      <div className={styles['registration-box']}>
        <h2>Register</h2>
        <form onSubmit={handleSubmit} noValidate>
          {['name','email','phone','address','password','confirmPassword'].map((field) => (
            <div key={field} className={styles['form-group']}>
              <label>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
              {field === 'address' ? (
                <textarea
                  name={field}
                  value={formData[field]}
                  onChange={handleChange}
                  className={errors[field] ? styles['invalid'] : ''}
                />
              ) : (
                <input
                  type={field.toLowerCase().includes('password') ? 'password' : 'text'}
                  name={field}
                  value={formData[field]}
                  onChange={handleChange}
                  className={errors[field] ? styles['invalid'] : ''}
                />
              )}
              {errors[field] && <div className={styles['error']}>{errors[field]}</div>}
            </div>
          ))}
          <button type="submit" className={styles['btn']}>Register</button>
        </form>
      </div>
    </div>
  );
};

export default Registration;
